import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutPlayer } from "../../actions/authActions";
import axios from "axios";



class MyAccount extends Component {


    state = {
        player: [],
        isLoading: true,
        errors: null
    };

    getPlayer() {
        axios.get('http://localhost:8000/test/getplayerById/' + this.props.auth._id)
            .then(response => {
                this.setState({
                    players: response.data.data,
                    isLoading:false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    componentDidMount() {
        this.getPlayer();
    }


    render() {
        const { isLoading, player } = this.state;


        return (
            <React.Fragment>
                <h3 className={"center-align"}>Leaderboard</h3>
                <div className= "center-align">
                    {!isLoading ? (
                        player.map(player => {
                            const { _id, elo, username, email } = player;
                            return (
                                <div key={_id}>
                                    { email }. {" "}
                                    {username} {" "} {" "}
                                    {elo}
                                    <hr />
                                </div>
                            );
                        })
                    ) : (
                        <p>Loading...</p>
                    )}
                </div>
            </React.Fragment>
        );
    }
}

MyAccount.propTypes = {
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(MyAccount);
