import axios from "axios";

const api = axios.create({
    baseURL: 'http://localhost:8000/riotapi'
})

export const loadLolacc = payload => api.get('/lolacc', payload)
export const loadRankedstats = payload => api.get('/loadRankedstats', payload)

const apis = {
    loadLolacc,
    loadRankedstats,
}

export default apis

