import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loadRankedstats } from "../../actions/riotStats";
import classnames from "classnames";



class MyStatistics extends Component {
    constructor() {
        super();
        this.state = {
            summonername: "",
        };
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    onSubmit = e => {
        e.preventDefault();
        const summonernamePlayer = {
            summonername: this.state.summonername,
        };
        console.log(summonernamePlayer)
        console.log(loadRankedstats(summonernamePlayer)); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
    };

    render() {
        const { player } = this.props.auth;

        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <h4>
                          My Statistics
                        </h4>

                        <form noValidate onSubmit={this.onSubmit}>
                            <label>
                                Summonername:
                                    <input
                                     onChange={this.onChange}
                                     value={this.state.summonername}
                                     id="summonername"
                                     type="summonername" />
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

MyStatistics.propTypes = {
    auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { loadRankedstats }
)(MyStatistics);
