import axios from "axios";

const api = axios.create({
    baseURL: 'http://localhost:8000/riotapi'
})
7/*
export const loadLolacc = payload => api.get('/lolacc', payload)
export const loadRankedstats = payload => api.get('/loadRankedstats', payload)

const apis = {
    loadLolacc,
    loadRankedstats,
}

export default apis
*/


export const loadLolacc = req => dispatch => {
    axios
        .get("http://localhost:8000/riotapi/lolacc", req)
        .catch(err =>
            dispatch({
                type: GET_ERRORS,
                payload: err.response.data
            })
        );
}

export const loadRankedstats = req => dispatch => {
    console.log(req.body)
    axios
        .get("http://localhost:8000/riotapi/rankedstats", req)
        .catch(err => dispatch({ type: GET_ERRORS, payload: err.response.data}));
}
