import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutPlayer } from "../../actions/authActions";
import axios from "axios";


class Forum extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    state = {
        forum: [],
        isLoading: true,
        errors: null,
    };


    createArticle() {
        axios.post('http://localhost:8000/forum/article/create')
            .then(response => {
                this.setState({
                    isLoading: false
                });
            })
        .catch(error => this.setState({ error, isLoading:false}));
    }

    getForum() {
        axios.get('http://localhost:8000/forum/article/get')
            .then(response => {
                console.log(response.data.data)
                this.setState({
                    forum: response.data.data,
                    isLoading:false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    getPlayer(author) {
        axios.get('http://localhost:8000/test/getplayer/' + author)
            .then(response => {
                this.setState({
                    playerData: response.data.data[0],
                    isLoading: false
                });
            })
            .catch(error => this.setState({error, isLoading: false}))
    }

    componentDidMount() {
        this.getForum();
    }




    render() {
        const { forum, isLoading } = this.state;
        const { player } = this.props.auth;


        return (
            <div className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <h4>
                            <b>Forum</b>
                        </h4>
                        <hr />
                    </div>
                    <div className= "center-align">
                        {!isLoading ? (
                            forum.map(articles => {
                                const { title, _id, article, author } = articles;
                                return (
                                    <div key={ _id }>
                                        <p> {title}</p>
                                        <p> {article}</p>
                                        <p> author: {this.getPlayer(author).username} </p>


                                        <hr />
                                    </div>
                                );
                            })
                        ) : (
                            <p>Loading...</p>
                        )}
                    </div>
                </div>
            </div>
        );
    }
}

Forum.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Forum);
