import React, { Component } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {logoutPlayer} from "../../actions/authActions";


class Leaderboard extends Component {

    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    state = {
        playerData: {},
        rankedstats: [],
        accountstats: [],
        isLoading: true,
        errors: null
    };

    getRankedStats() {
        axios.get('http://localhost:8000/riotapi/rankedstats/' + this.props.auth.player.id)
            .then(response => {
                this.setState({
                    rankedstats: response.data.body,
                    isLoading:false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    getAccountStats() {
        axios.get('http://localhost:8000/riotapi/lolacc/' + this.props.auth.player.id)
            .then(response => {
                this.setState({
                    accountstats: response.data.body,
                    isLoading: false
                });
            })
            .catch(error => this.setState({error, isLoading: false}))
    }

    getPlayer() {
        axios.get('http://localhost:8000/test/getplayer/' + this.props.auth.player.id)
            .then(response => {
                this.setState({
                    playerData: response.data.data,
                    isLoading: false
                });
            })
            .catch(error => this.setState({error, isLoading: false}))
    }

    componentDidMount() {
        this.getAccountStats();
        this.getRankedStats();
        this.getPlayer();
    }



    render() {
        const { isLoading, rankedstats, accountstats, playerData } = this.state;
        var loggedIn = this.props.auth.isAuthenticated;

        var playerwithoutSummoner = true;
        var rankedstatsUndefined = true;

        if(rankedstats == undefined) {
            rankedstatsUndefined = true;
        }
        else {
            rankedstatsUndefined = false;
        }

        console.log("playerData:");
        console.log(playerData);
        console.log("accountstats:");
        console.log(accountstats);

        return (
            <div>
                {loggedIn ? (
                    <React.Fragment>
                        <h3 className={"center-align"}>My Stats</h3>
                            <div>
                                {rankedstatsUndefined ? (
                                    <p className={"center-align"}> Too many requests </p>
                                ) : (
                                    <div className= "center-align">
                                        {!isLoading ? (
                                            rankedstats.map(player => {
                                                const { tier, rank, summonerName, queueType } = player;
                                                return (
                                                    <div key={ queueType }>
                                                        { queueType } {" "}
                                                        { tier } {" "}
                                                        { rank } {" "} {" "}
                                                        { summonerName }
                                                        <hr />
                                                    </div>
                                                );
                                            })
                                        ) : (
                                            <p>Loading...</p>
                                        )}
                                    </div>
                                )}
                            </div>
                    </React.Fragment>
                ) : (
                    <p className={"center-align"}> You have to to login first</p>
                )}

            </div>



        );
    }
}



Leaderboard.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Leaderboard);
