import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loadRankedstats } from "../../actions/riotStats";
import classnames from "classnames";



class MyStatistics extends Component {
    constructor() {
        super();
        this.state = {
            summonername: "",
        };
    }

    componentDidMount() {
        // If logged in and player navigates to Login page, should redirect them to dashboard
        if (this.props.auth.isAuthenticated) {
            this.props.history.push("/dashboard");
        }
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    onSubmit = e => {
        e.preventDefault();
        const summonernamePlayer = {
            summonername: this.state.summonername,
        };
        console.log(summonernamePlayer)
    };

    render() {
        const { player } = this.props.auth;

        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <h4>
                          My Statistics
                        </h4>

                        <form noValidate onSubmit={this.onSubmit}>
                            <label>
                                Summonername:
                                    <input
                                     onChange={this.onChange}
                                     value={this.state.summonername}
                                     id="summonername"
                                     type="summonername" />
                            </label>
                            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                                <button
                                    style={{
                                        width: "150px",
                                        borderRadius: "3px",
                                        letterSpacing: "1.5px",
                                        marginTop: "1rem"
                                    }}
                                    type="submit"
                                    className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                                >
                                    Login
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

MyStatistics.propTypes = {
    loadRankedstats: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { loadRankedstats }
)(MyStatistics);
