import React, { Component } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {logoutPlayer} from "../../actions/authActions";


class Leaderboard extends Component {

    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    state = {
        summonername: "Lesoge",
        rankedstats: [],
        accountstats: [],
        isLoading: true,
        errors: null
    };

    getRankedStats() {
        axios.get('http://localhost:8000/riotapi/rankedstats/' + this.props.auth.player.summonername)
            .then(response => {
                this.setState({
                    rankedstats: response.data,
                    isLoading:false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    getAccountStats(summonername) {
        axios.get('http://localhost:8000/riotapi/lolacc')
            .then(response => {
                console.log(response.data);
                this.setState({
                    accountstats: response.data,
                    isLoading: false
                });
            })
            .catch(error => this.setState({error, isLoading: false}))
    }

    componentDidMount() {
        this.getAccountStats();
        this.getRankedStats("Lesoge");
    }



    render() {
        const { isLoading, rankedstats, accountstats } = this.state;


        return (

            <React.Fragment>
                <h3 className={"center-align"}>My Stats</h3>
                <div className= "center-align">
                    {!isLoading ? (
                        rankedstats.map(player => {
                            const { tier, rank, summonerName, summonerId } = player;
                            return (
                                <div key={ summonerId }>
                                    { tier }. {" "}
                                    { rank } {" "} {" "}
                                    { summonerName}
                                    <hr />
                                </div>
                            );
                        })
                    ) : (
                        <p>Loading...</p>
                    )}
                </div>
            </React.Fragment>
        );
    }
}



Leaderboard.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Leaderboard);
