import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutPlayer } from "../../actions/authActions";
import axios from "axios";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";


class Forumcreate extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            text: 'Enter Text',
            author: 'Enter Author',
            title: 'Enter Title',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleChange2(event) {
        this.setState({value: event.target.value});
    }

    handleChange3(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.value);
        event.preventDefault();
    }

    createArticle() {
        axios.post('http://localhost:8000/forum/article/create')
            .then(response => {
                this.setState({
                    isLoading: false
                });
            })
            .catch(error => this.setState({ error, isLoading:false}));
    }

    componentDidMount() {
    }


    render() {
        const { player } = this.props.auth;

        return (
            <div className={"center-align"}>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <p> Title: </p>
                        <input type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <label>
                        <p> Article: </p>
                        <textarea value={this.state.value} onChange={this.handleChange2} />
                    </label>
                    <label>
                        <p> Author: </p>
                        <input type="text" value={this.state.value} onChange={this.handleChange3} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}

Forumcreate.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Forumcreate);
