import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutPlayer } from "../../actions/authActions";
import axios from "axios";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";


class Forumcreate extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    state = {
        forum: [],
        isLoading: true,
        errors: null,
    };


    createArticle() {
        axios.post('http://localhost:8000/forum/article/create')
            .then(response => {
                this.setState({
                    isLoading: false
                });
            })
            .catch(error => this.setState({ error, isLoading:false}));
    }

    componentDidMount() {
        this.getForum();
    }


    render() {
        const { forum, isLoading } = this.state;
        const { player } = this.props.auth;

        return (
            <div className="container valign-wrapper">
                Test
            </div>
        );
    }
}

Forumcreate.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Forumcreate);
