import React, { Component } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loadPlayer } from "../../actions/leaderboard";
import Table from "react-bootstrap/Table";


class Leaderboard extends Component {

    state = {
        players: [],
        isLoading: true,
        errors: null
    };

    getPlayers() {
        axios.get('http://localhost:8000/test/getplayers')
            .then(response => {
                console.log(response)
                this.players = response.data
                console.log(this.players)
            })
            .then(players => {
                this.setState({
                    players,
                    isLoading: false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    componentDidMount() {
        this.getPlayers();
    }

    /*
        componentDidMount()
        {
            axios.get('http://localhost:8000/test/getplayers')
                .then(response => {
                    console.log(response.data.data)
                    this.props({data: response.data.data});
                })
                .catch(error => {
                    console.log(error);
                })
        }
    */


    render() {
        const { player } = this.props.auth;
        const { isLoading, players } = this.state;


        return (


            <React.Fragment>
                <h2>Leaderboard</h2>
                <div>
                    { players.map(function(d, idx){
                        return (<li key={idx}> {d.elo} </li>)
                    })}
                </div>
            </React.Fragment>

        );
    }
}



const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { loadPlayer }
)(Leaderboard);
