import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutPlayer } from "../../actions/authActions";
import axios from "axios";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";


class Forumcreate extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutPlayer();
    };

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            value: 'Enter Text',
            author: 'Enter Author',
            title: 'Enter Title',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }


    handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.value);
        event.preventDefault();
    }

    createArticle() {
        axios.post('http://localhost:8000/forum/article/create')
            .then(response => {
                this.setState({
                    isLoading: false
                });
            })
            .catch(error => this.setState({ error, isLoading:false}));
    }

    componentDidMount() {
    }


    render() {
        const { player } = this.props.auth;

        return (
            <div className={"center-align"} style={{ width: "25%", margin: "auto"}}>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <p> Title: </p>
                        <input type="text" name="title" placeholder={"Title"} value={this.state.title} onChange={this.handleChange.bind(this)}/>
                    </label>
                    <label style={{ height: "500px" }}>
                        <p> Article: </p>
                        <textarea name="text" value={this.state.value} onChange={this.handleChange.bind(this)} />
                    </label>
                    <label>
                        <p> Author: </p>
                        <input type="text" name="author" value={this.state.author} onChange={this.handleChange.bind(this)} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}

Forumcreate.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutPlayer }
)(Forumcreate);
