import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loadRankedstats } from "../../actions/riotStats";
import classnames from "classnames";



class MyStatistics extends Component {
    constructor() {
        super();
        this.state = {
            summonername: "",
            errors: {}
        };
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    onSubmit = e => {
        e.preventDefault();
        const summonernamePlayer = {
            summonername: this.state.summonername,
        };

        this.props.loadRankedstats(summonernamePlayer); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
    };

    render() {
        const { player } = this.props.auth;
        const { errors } = this.state;

        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <h4>
                          My Statistics
                        </h4>

                        <form>
                            <label>
                                Summonername:
                                <input
                                    onChange={this.onChange}
                                    value={this.state.summonername}
                                    error={errors.summonername}
                                    id="summonername"
                                    type="summonername"
                                    className={classnames("", {
                                        invalid: errors.summonername || errors.summonernamenotfound
                                    })} />
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

MyStatistics.propTypes = {
    logoutPlayer: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { loadRankedstats }
)(MyStatistics);
