import React, { Component } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loadPlayer } from "../../actions/leaderboard";
import Table from "react-bootstrap/Table";


class Leaderboard extends Component {

    state = {
        players: [],
        isLoading: true,
        errors: null
    };

    getPlayers() {
        axios.get('http://localhost:8000/test/getplayers')
            .then(response => {
                console.log(response)

                this.setState({
                    players: response.data.data,
                    isLoading:false
                });
            })
            .catch(error => this.setState({ error, isLoading: false}))
    }

    componentDidMount() {
        this.getPlayers();
    }

    /*
        componentDidMount()
        {
            axios.get('http://localhost:8000/test/getplayers')
                .then(response => {
                    console.log(response.data.data)
                    this.props({data: response.data.data});
                })
                .catch(error => {
                    console.log(error);
                })
        }
    */


    render() {
        const { player } = this.props.auth;
        const { isLoading, players } = this.state;


        return (


            <React.Fragment>
                <h3>Leaderboard</h3>

                <div>
                    {!isLoading ? (

                        players.map(players => {
                            const { email, username, elo} = player;
                            return (
                                <div key={email}>
                                    {username}
                                    {elo}
                                    <hr />
                                </div>
                            );
                        })
                    ) : (
                        <p>Loading...</p>
                    )}
                </div>
            </React.Fragment>
        );
    }
}



const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { loadPlayer }
)(Leaderboard);
